def main():
    try:
        with open('animal_stats.json', 'r', encoding='utf-8') as f:
            animals = json.load(f)
    except FileNotFoundError:
        animals = []

    existing_names = get_existing_names(animals)
    added_count = 0

    for data in new_animals_data:
        if data[0] not in existing_names:
            new_animal = create_animal(*data)
            animals.append(new_animal)
            added_count += 1
            print(f"Added {data[0]}")
        else:
            print(f"Skipped {data[0]} (already exists)")

    with open('animal_stats.json', 'w', encoding='utf-8') as f:
        json.dump(animals, f, indent=2, ensure_ascii=False)

    print(f"\nSuccessfully added {added_count} new animals!")

if __name__ == "__main__":
    main()
